import React, { useState } from "react";

// const btnMenuBar = document.querySelector(".btnMenuList-1")
function Header() {
  const [menubarLang, setMenubarLang] = useState(1);
  const [isOpen, setIsOpen] = useState(false);
  const [isOpenMenu, setIsOpenMenu] = useState(false);
  ocnst

  const toggleLangList = () => {
    setIsOpen(!isOpen);
  };

  const toggleMenuList = () => {
    setIsOpenMenu(!isOpenMenu);
  };

  return (
    <header className="header">
      <div className="header-1">
        <a href="https://doubletapp.ai/en" className="logo">
          <span className="logo-text">
            DOUBLETAPP
            <div className="logo-dot"></div>
          </span>
        </a>
        <div className="btn-change-light">
          <input type="checkbox" className="btnOff" id="desktopToggle" />
          <label htmlFor="desktopToggle" className="btnLabelChange">
            <div className="btnChange-1">
              <div className="btnChange-circle btnChange-pos"></div>
            </div>
            <span className="btnChangeText">light side</span>
          </label>
        </div>
        <div
          className="btn-change-lang-1 btn-change-lang-2"
          onClick={toggleLangList}
        >
          <button
            className="btnLangCircle btnLangCircle-2 justForBtn"
            type="button"
            aria-expanded="true"
          >
            <span className="btnOff">Change language</span>
          </button>
          {isOpen && (
            <ul className="lang-pos-1 lang-pos-2 lang-type">
              <li>
                <button
                  className="lang-btn lang-btn-size justForBtn"
                  type="button"
                  value="ru"
                  onClick={() => {
                    setMenubarLang(1);
                    toggleLangList();
                  }}
                >
                  Русский
                </button>
              </li>
              <li>
                <button
                  className="lang-btn lang-btn-size justForBtn"
                  type="button"
                  value="en"
                  onClick={() => {
                    setMenubarLang(2);
                    toggleLangList();
                  }}
                >
                  English
                </button>
              </li>
            </ul>
          )}
        </div>
        <button
          aria-label="Открыть меню"
          onClick={toggleMenuList}
          aria-expanded="false"
          className={`btnMenuList-1 btnMenuList-2 btnMenuList-3 justForBtn ${
            isOpenMenu ? "active-menuBar" : ""
          }`}
        >
          <span className="btnOff">Открыть меню</span>
        </button>
        <div className="blockMenu-1 blockMenu-2"></div>
      </div>
      <div className="headerNav-1 headerNav-2">
        <div className="blockNav-1 blockNav-2 blockNav-3">

        </div>
      </div>
    </header>
  );
}

export default Header;
