import React from 'react'

function Images({idClicked, idService_1, idService_2, service}) {
  return (
    <>
        <div
              className="PhotoService"
              style={{ transform: `scale(1)`, transformOrigin: `right center` }}
            >
              <div className="blockForImage">
                {service.map((o) => (
                  <>
                    {(o.id === idService_1) && (
                      <>
                        <div
                          key={o.rotate_1}
                          className={`image-service-${o.i++} typeOfImageService beforeImage`}
                          style={{
                            opacity: `${o.id === idClicked ? 1 : 0}`,
                            transform: `translateY(${
                              o.id === idClicked ? `0px` : `119px`
                            })`,
                            rotate: `${o.rotate_1}deg`,
                            transition: 'opacity 0.5s ease-out, transform 0.5s ease-out',
                          }}
                        >
                          <img
                            src={`${o.img_1}`}
                            alt="Service-Img"
                            sizes="100vw"
                            decoding="async"
                            data-nimg="fill"
                            loading="lazy"
                            className="mobileService"
                            style={{
                              position: `absolute`,
                              height: `100%`,
                              width: `100%`,
                              color: `transparent`,
                              inset: `0px`,
                            }}
                          />
                        </div>
                        <div
                          key={o.rotate_2}
                          className={`image-service-${o.i++} typeOfImageService`}
                          style={{
                            opacity: `${o.id === idClicked ? 1 : 0}`,
                            transform: `translateY(${
                              o.id === idClicked ? `0px` : `119px`
                            })`,
                            rotate: `${o.rotate_2}deg`,
                            transition: 'opacity 0.5s ease-out, transform 0.5s ease-out',
                          }}
                        >
                          <img
                            src={`${o.img_2}`}
                            alt="Service-Img"
                            sizes="100vw"
                            decoding="async"
                            data-nimg="fill"
                            loading="lazy"
                            className="mobileService"
                            style={{
                              position: `absolute`,
                              height: `100%`,
                              width: `100%`,
                              color: `transparent`,
                              inset: `0px`,
                            }}
                          />
                        </div>
                      </>
                    )}

                    {(o.id === idService_2)  && (
                      <>
                        <div
                          key={o.rotate_1}
                          className={`image-service-${o.letters[0]} typeOfImageService`}
                          style={{
                            opacity: `${o.id === idClicked ? 1 : 0}`,
                            transform: `translateY(${
                              o.id === idClicked ? `0px` : `119px`
                            })`,
                            rotate: `${o.rotate_1}deg`,
                            transition: 'opacity 0.5s ease-out, transform 0.5s ease-out',
                          }}
                        >
                          <img
                            src={`${o.img_1}`}
                            alt="Service-Img"
                            sizes="100vw"
                            decoding="async"
                            data-nimg="fill"
                            loading="lazy"
                            className="mobileService"
                            style={{
                              position: `absolute`,
                              height: `100%`,
                              width: `100%`,
                              color: `transparent`,
                              inset: `0px`,
                            }}
                          />
                        </div>
                        <div
                          key={o.rotate_2}
                          className={`image-service-${o.letters[1]} typeOfImageService`}
                          style={{
                            opacity: `${o.id === idClicked ? 1 : 0}`,
                            transform: `translateY(${
                              o.id === idClicked ? `0px` : `119px`
                            })`,
                            rotate: `${o.rotate_2}deg`,
                            transition: 'opacity 0.5s ease-out, transform 0.5s ease-out',
                          }}
                        >
                          <img
                            src={`${o.img_2}`}
                            alt="Service-Img"
                            sizes="100vw"
                            decoding="async"
                            data-nimg="fill"
                            loading="lazy"
                            className="mobileService"
                            style={{
                              position: `absolute`,
                              height: `100%`,
                              width: `100%`,
                              color: `transparent`,
                              inset: `0px`,
                            }}
                          />
                        </div>
                        <div
                          key={o.rotate_3}
                          className={`image-service-${o.letters[2]} typeOfImageService`}
                          style={{
                            opacity: `${o.id === idClicked ? 1 : 0}`,
                            transform: `translateY(${
                              o.id === idClicked ? `0px` : `119px`
                            })`,
                            rotate: `${o.rotate_3}deg`,
                            transition: 'opacity 0.5s ease-out, transform 0.5s ease-out',
                          }}
                        >
                          <img
                            src={`${o.img_3}`}
                            alt="Service-Img"
                            sizes="100vw"
                            decoding="async"
                            data-nimg="fill"
                            loading="lazy"
                            className="mobileService"
                            style={{
                              position: `absolute`,
                              height: `100%`,
                              width: `100%`,
                              color: `transparent`,
                              inset: `0px`,
                            }}
                          />
                        </div>
                      </>
                    )}
                  </>
                ))}
              </div>
            </div>
    </>
  )
}

export default Images
