              <Link key={o.id} to={o.href} className="typeOfLink sizeOfLink" onClick={()=> dispatch(toggleMenuList())}>{o.txt}
                {o.id===5 ? <span className="typeLink-1 sizeOfLink-1 ChVisible">{o.after_txt}</span> : ''}
              </Link>
